import React, { useContext, useState, useRef, useEffect } from 'react'
import im from 'D:/VS_Code/VS_Code_general_Workspace/finalYearProject/chatApp/src/images/im.jpg'
import { AuthContext } from '../context/AuthContext'
import { ChatContext } from '../context/ChatContext'
import { Timestamp, arrayUnion, doc, serverTimestamp, updateDoc } from 'firebase/firestore'
import { db, storage } from '../firebase'
import { v4 as uuid } from 'uuid'
// import Jimp from 'jimp'
import { getDownloadURL, ref, uploadBytesResumable } from 'firebase/storage'

const Input = () => {
  const [text,setText]=useState("")
  const [img,setImg]=useState(null)
  const {currentUser}=useContext(AuthContext)
  const {data}=useContext(ChatContext)


  function embedMessageInImage(imageURL, message) {
    return new Promise((resolve, reject) => {
      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');
      const image = new Image();
  
      image.onload = function() {
        canvas.width = image.width;
        canvas.height = image.height;
        context.drawImage(image, 0, 0);
        
        // Convert the message to binary
        const binaryMessage = Array.from(Buffer.from(message, 'utf-8'))
          .map(byte => byte.toString(2).padStart(8, '0')).join('');
  
        // Embed the message into the image pixel by pixel
        let bitIndex = 0;
        for (let y = 0; y < canvas.height; y++) {
          for (let x = 0; x < canvas.width; x++) {
            const pixel = context.getImageData(x, y, 1, 1).data;
  
            if (bitIndex < binaryMessage.length) {
              const bit = binaryMessage.charAt(bitIndex);
              // Set the least significant bit of each channel to the message bit
              pixel[0] = (pixel[0] & 0xfe) | (bit.charCodeAt(0) & 0x01);
              pixel[1] = (pixel[1] & 0xfe) | (bit.charCodeAt(0) & 0x01);
              pixel[2] = (pixel[2] & 0xfe) | (bit.charCodeAt(0) & 0x01);
              context.fillStyle = `rgb(${pixel[0]}, ${pixel[1]}, ${pixel[2]})`;
              context.fillRect(x, y, 1, 1);
              bitIndex++;
            }
          }
        }
  
        // Convert the canvas image to data URL
        const modifiedImageURL = canvas.toDataURL();
        resolve(modifiedImageURL);
      };
  
      image.onerror = function() {
        reject(new Error('Failed to load image.'));
      };
  
      image.src = imageURL;
    });
  }


  function decodeMessageFromImage(modifiedImageURL) {
    return new Promise((resolve, reject) => {
      const image = new Image();
  
      image.crossOrigin = 'anonymous'; // Allow cross-origin image loading
  
      image.onload = function() {
        const canvas = document.createElement('canvas');
        canvas.width = image.width;
        canvas.height = image.height;
  
        const context = canvas.getContext('2d');
        context.drawImage(image, 0, 0);
  
        let binaryMessage = '';
        let bitIndex = 0;
  
        for (let y = 0; y < canvas.height; y++) {
          for (let x = 0; x < canvas.width; x++) {
            const pixel = context.getImageData(x, y, 1, 1).data;
  
            if (bitIndex < 8) {
              const bit = (pixel[0] & 0x01).toString();
              binaryMessage += bit;
              bitIndex++;
            }
          }
        }
  
        const messageBytes = [];
        for (let i = 0; i < binaryMessage.length; i += 8) {
          const byte = parseInt(binaryMessage.substr(i, 8), 2);
          messageBytes.push(byte);
        }
  
        const originalMessage = Buffer.from(messageBytes).toString('utf-8');
        resolve(originalMessage);
      };
  
      image.onerror = function() {
        reject(new Error('Failed to load image.'));
      };
  
      image.src = modifiedImageURL;
    });
  }
  



  const handleSend=async()=>{
    if(img){
        const storageRef = ref(storage, uuid());
        const uploadTask = uploadBytesResumable(storageRef, img); 
        
        const reader = new FileReader();
        reader.onload = async () => {
        const imageDataUrl = reader.result;
            embedMessageInImage(imageDataUrl, message)
        .then((modifiedImageURL) => {
          console.log('Modified Image URL:', modifiedImageURL);
        })
        .catch((error) => {
          console.error('Error embedding message:', error);
        });
        }
      reader.readAsDataURL(img);


        uploadTask.on(
          (error) => {
            // setErr(true);
          },
          () => {
            getDownloadURL(uploadTask.snapshot.ref).then(async (downloadURL) => {
              await updateDoc(doc(db, "chats", data.chatId), {
                messages: arrayUnion({
                  id: uuid(),
                  text,
                  senderId: currentUser.uid,
                  date: Timestamp.now(),
                  img: downloadURL,
                }),
              });
            });
          }
        );
    } else{
      await updateDoc(doc(db,"chats",data.chatId),{
        messages:arrayUnion({
          id:uuid(),
          text,
          senderId:currentUser.uid,
          date:Timestamp.now()
        })
      })
    }
    await updateDoc(doc(db,"userChats",currentUser.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })
    await updateDoc(doc(db,"userChats",data.user.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })

    setText("")
    setImg(null)
  }
  const handleChange=(e)=>{
    setImg(e.target.files[0])
  }
  return (
    <div className='input'>
      <input type="text" placeholder='Type...' onChange={e=>setText(e.target.value)} value={text} />
      <div className="send">
        <input type="file" style={{display:'none'}} id='file' onChange={handleChange}/>
        <label htmlFor="file">
          <img src={im} alt=""/>
        </label>
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  )
}
export default Input
