import React, { useContext, useEffect, useState } from 'react'
import icon from "D:/VS_Code/VS_Code_general_Workspace/finalYearProject/chatApp/src/images/user.jpg"
import { doc, onSnapshot } from "firebase/firestore";
import { AuthContext } from '../context/AuthContext';
import {db} from '../firebase'


const Chats = () => {
  const [chats,setChats]=useState([])
  const {currentUser}=useContext(AuthContext)

  useEffect(()=>{

    const unsub = onSnapshot(doc(db, "userChats", currentUser.uid), (doc) => {
        setChats(doc.data())
    });
    return ()=>{
      unsub();
    };
  },[currentUser.uid]);
  console.log(chats)
  return (
    <div className='chats'>
      <div className="userChat">
          <img src={icon} alt="" />
          <div className="userChatInfo">
            <span>Name1</span>
            <p>Hello</p>
          </div>
        </div>
      <div className="userChat">
          <img src={icon} alt="" />
          <div className="userChatInfo">
            <span>Name1</span>
            <p>Hello</p>
          </div>
        </div>
      <div className="userChat">
          <img src={icon} alt="" />
          <div className="userChatInfo">
            <span>Name1</span>
            <p>Hello</p>
          </div>
        </div>
      <div className="userChat">
          <img src={icon} alt="" />
          <div className="userChatInfo">
            <span>Name1</span>
            <p>Hello</p>
          </div>
        </div>
    </div>
  )
}
export default Chats
