import React, { useContext, useState} from 'react'
import im from 'D:/VS_Code/VS_Code_general_Workspace/finalYearProject/chatApp/src/images/im.jpg'
import { AuthContext } from '../context/AuthContext'
import { ChatContext } from '../context/ChatContext'
import { Timestamp, arrayUnion, doc, serverTimestamp, updateDoc } from 'firebase/firestore'
import { db, storage } from '../firebase'
import { v4 as uuid } from 'uuid'
// import Jimp from 'jimp'
import { getDownloadURL, ref, uploadBytesResumable } from 'firebase/storage'

const Input = () => {
  const [text,setText]=useState("")
  const [img,setImg]=useState(null)
  const {currentUser}=useContext(AuthContext)
  const {data}=useContext(ChatContext)
  // const steganography = require('./steganogarphy');
  const util = require("util");
const Image = require("canvas").Image;
const ImageEncoder = require("canvas").ImageEncoder;

function embedMessage(imageURL, message) {
  // Create a new canvas
  const canvas = document.createElement("canvas");
  // Create a new image from the imageURL
  const img = new Image();
  img.crossOrigin = "anonymous";
  img.src = imageURL;
  // Set the width and height of the canvas to the same as the image
  canvas.width = img.width;
  canvas.height = img.height;
  // Create a 2D context for the canvas
  const ctx = canvas.getContext("2d");
  // Draw the image onto the canvas
  ctx.drawImage(img, 0, 0);
  // Convert the canvas to a data URL
  const dataURL = canvas.toDataURL("image/png");
  // Split the dataURL into its components
  const components = dataURL.split(",");
  // Get the image data
  const imageData = components[1];
  // Convert the image data to a Uint8Array
  const uint8Array = util.parseBinary(imageData);
  // Get the length of the message
  const messageLength = message.length;
  // Iterate over the message characters
  for (let i = 0; i < messageLength; i++) {
    // Get the current character
    const character = message[i];
    // Convert the character to its ASCII code
    const asciiCode = character.charCodeAt(0);
    // Update the image data with the ASCII code
    uint8Array[i] = asciiCode;
  }
  // Convert the Uint8Array back to a dataURL
  const newDataURL = util.encodeBinary(uint8Array);
  // Return the new dataURL
  return newDataURL;
}



  const handleSend=async()=>{
    if(img){
        const storageRef = ref(storage, uuid());
        const uploadTask = uploadBytesResumable(storageRef, img); 
        
        const reader = new FileReader();
        reader.onload = async () => {
        const imageDataUrl = reader.result;
        // const newURL=embedMessage(imageDataUrl,"hello")
        //   console.log(decodeMessage(newURL))

        }
      reader.readAsDataURL(img);


        uploadTask.on(
          (error) => {
            // setErr(true);
          },
          () => {
            getDownloadURL(uploadTask.snapshot.ref).then(async (downloadURL) => {
              await updateDoc(doc(db, "chats", data.chatId), {
                messages: arrayUnion({
                  id: uuid(),
                  text,
                  senderId: currentUser.uid,
                  date: Timestamp.now(),
                  img: downloadURL,
                }),
              });
            });
          }
        );
    } else{
      await updateDoc(doc(db,"chats",data.chatId),{
        messages:arrayUnion({
          id:uuid(),
          text,
          senderId:currentUser.uid,
          date:Timestamp.now()
        })
      })
    }
    await updateDoc(doc(db,"userChats",currentUser.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })
    await updateDoc(doc(db,"userChats",data.user.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })

    setText("")
    setImg(null)
  }
  const handleChange=(e)=>{
    setImg(e.target.files[0])
  }
  return (
    <div className='input'>
      <input type="text" placeholder='Type...' onChange={e=>setText(e.target.value)} value={text} />
      <div className="send">
        <input type="file" style={{display:'none'}} id='file' onChange={handleChange}/>
        <label htmlFor="file">
          <img src={im} alt=""/>
        </label>
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  )
}
export default Input
