import React, { useContext, useState, useRef, useEffect } from 'react'
import im from 'D:/VS_Code/VS_Code_general_Workspace/finalYearProject/chatApp/src/images/im.jpg'
import { AuthContext } from '../context/AuthContext'
import { ChatContext } from '../context/ChatContext'
import { Timestamp, arrayUnion, doc, serverTimestamp, updateDoc } from 'firebase/firestore'
import { db, storage } from '../firebase'
import { v4 as uuid } from 'uuid'
// import Jimp from 'jimp'
import { getDownloadURL, ref, uploadBytesResumable } from 'firebase/storage'

const Input = () => {
  const [text,setText]=useState("")
  const [img,setImg]=useState(null)
  const {currentUser}=useContext(AuthContext)
  const {data}=useContext(ChatContext)


  
  function embedMessageInImage(imageURL, message) {
    return new Promise((resolve, reject) => {
      const canvas = document.createElement('canvas');
      const image = new Image();
  
      image.crossOrigin = 'anonymous'; // Allow cross-origin image loading
      image.onload = function () {
        canvas.width = image.width;
        canvas.height = image.height;
  
        const context = canvas.getContext('2d');
        context.drawImage(image, 0, 0);
  
        const messageLength = message.length;
        const encodedMessage = [messageLength, ...message].map((char) =>
          char.charCodeAt(0)
        );
  
        let messageIndex = 0;
        let messageEnd = false;
  
        for (let y = 0; y < canvas.height; y++) {
          for (let x = 0; x < canvas.width; x++) {
            const pixel = context.getImageData(x, y, 1, 1).data;
  
            if (!messageEnd) {
              let [red, green, blue] = pixel;
  
              // Embed the message bits into the least significant bits of the red, green, and blue channels
              if (messageIndex < encodedMessage.length) {
                const char = encodedMessage[messageIndex];
                red = (red & 0b11111110) | ((char & 0b10000000) >> 7);
                green = (green & 0b11111110) | ((char & 0b01000000) >> 6);
                blue = (blue & 0b11111110) | ((char & 0b00100000) >> 5);
  
                encodedMessage[messageIndex] = char << 1;
  
                messageIndex++;
              } else {
                messageEnd = true;
              }
  
              // Update the pixel with the modified RGB values
              context.fillStyle = `rgb(${red}, ${green}, ${blue})`;
              context.fillRect(x, y, 1, 1);
            }
          }
  
          if (messageEnd) {
            break;
          }
        }
  
        const modifiedImageURL = canvas.toDataURL('image/jpeg');
        resolve(modifiedImageURL);
      };
  
      image.onerror = function () {
        reject(new Error('Failed to load image.'));
      };
  
      image.src = imageURL;
    });
  }
    
  
  
  function decodeMessageFromImage(modifiedImageURL) {
    return new Promise((resolve, reject) => {
      const image = new Image();
      image.crossOrigin = 'anonymous'; // Allow cross-origin image loading
  
      image.onload = function () {
        const canvas = document.createElement('canvas');
        canvas.width = image.width;
        canvas.height = image.height;
  
        const context = canvas.getContext('2d');
        context.drawImage(image, 0, 0);
  
        const messageBytes = [];
        let messageLength = 0;
        let messageEnd = false;
  
        for (let y = 0; y < canvas.height; y++) {
          for (let x = 0; x < canvas.width; x++) {
            const pixel = context.getImageData(x, y, 1, 1).data;
  
            if (!messageEnd) {
              const red = pixel[0];
              const green = pixel[1];
              const blue = pixel[2];
  
              // Extract the least significant bits from the red, green, and blue channels
              const char =
                ((red & 0b00000001) << 7) |
                ((green & 0b00000001) << 6) |
                ((blue & 0b00000001) << 5);
  
              if (messageLength === 0) {
                messageLength = char;
              } else {
                messageBytes.push(char);
  
                if (messageBytes.length === messageLength) {
                  messageEnd = true;
                  break;
                }
              }
            }
          }
  
          if (messageEnd) {
            break;
          }
        }
  
        if (messageEnd) {
          const originalMessage = String.fromCharCode(...messageBytes);
          resolve(originalMessage);
        } else {
          reject(new Error('No message found in the image.'));
        }
      };
  
      image.onerror = function () {
        reject(new Error('Failed to load image.'));
      };
  
      image.src = modifiedImageURL;
    });
  }
  
  




  const handleSend=async()=>{
    if(img){
        const storageRef = ref(storage, uuid());
        const uploadTask = uploadBytesResumable(storageRef, img); 
        
        const reader = new FileReader();
        reader.onload = async () => {
        const imageDataUrl = reader.result;
        embedMessageInImage(imageDataUrl, "hello")
    .then((modifiedImageURL) => {
      console.log('Modified Image URL:', modifiedImageURL);
      return decodeMessageFromImage(modifiedImageURL);
    })
    .then((originalMessage) => {
      console.log('Original Message:', originalMessage);
    })
    .catch((error) => {
      console.error('Error:', error);
    });


        }
      reader.readAsDataURL(img);


        uploadTask.on(
          (error) => {
            // setErr(true);
          },
          () => {
            getDownloadURL(uploadTask.snapshot.ref).then(async (downloadURL) => {
              await updateDoc(doc(db, "chats", data.chatId), {
                messages: arrayUnion({
                  id: uuid(),
                  text,
                  senderId: currentUser.uid,
                  date: Timestamp.now(),
                  img: downloadURL,
                }),
              });
            });
          }
        );
    } else{
      await updateDoc(doc(db,"chats",data.chatId),{
        messages:arrayUnion({
          id:uuid(),
          text,
          senderId:currentUser.uid,
          date:Timestamp.now()
        })
      })
    }
    await updateDoc(doc(db,"userChats",currentUser.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })
    await updateDoc(doc(db,"userChats",data.user.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })

    setText("")
    setImg(null)
  }
  const handleChange=(e)=>{
    setImg(e.target.files[0])
  }
  return (
    <div className='input'>
      <input type="text" placeholder='Type...' onChange={e=>setText(e.target.value)} value={text} />
      <div className="send">
        <input type="file" style={{display:'none'}} id='file' onChange={handleChange}/>
        <label htmlFor="file">
          <img src={im} alt=""/>
        </label>
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  )
}
export default Input
