import React, { useContext, useState } from 'react'
import im from 'D:/VS_Code/VS_Code_general_Workspace/finalYearProject/chatApp/src/images/im.jpg'
import { AuthContext } from '../context/AuthContext'
import { ChatContext } from '../context/ChatContext'
import { Timestamp, arrayUnion, doc, serverTimestamp, updateDoc } from 'firebase/firestore'
import { db, storage } from '../firebase'
import { v4 as uuid } from 'uuid'
import { Jimp } from 'jimp'
import { getDownloadURL, ref, uploadBytesResumable } from 'firebase/storage'

const Input = () => {
  const [text,setText]=useState("")
  const [img,setImg]=useState(null)
  const {currentUser}=useContext(AuthContext)
  const {data}=useContext(ChatContext)
  // const [path,setPath]=useState("")


const encodeMessage = (imageURL, message) => {

  // console.log("encode message");
  // console.log(image);
  // const bitmap = new ImageBitmap(image)
  // console.log(bitmap.width);
  Jimp.read(imageURL).then(image=>{
      const width = image.bitmap.width;
      const height = image.bitmap.height;
      console.log(width)
      console.log(height)

      const binaryMessage = message
        .split('')
        .map(char => char.charCodeAt(0).toString(2).padStart(8, '0'))
        .join('');

      // Add the delimiter to the binary message
      const binaryMessageWithDelimiter = binaryMessage + '0000000000';
      
      const modifiedImage=image;
      let bitIndex = 0;
      for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
          const rgba = Jimp.intToRGBA(image.getPixelColor(x, y));
          const binaryR = rgba.r.toString(2).padStart(8, '0');
          const binaryG = rgba.g.toString(2).padStart(8, '0');
          const binaryB = rgba.b.toString(2).padStart(8, '0');
          
          const modifiedR = binaryR.slice(0, 7) + binaryMessageWithDelimiter[bitIndex];
          const modifiedG = binaryG.slice(0, 7) + binaryMessageWithDelimiter[bitIndex + 1];
          const modifiedB = binaryB.slice(0, 7) + binaryMessageWithDelimiter[bitIndex + 2];

          const modifiedColor = Jimp.rgbaToInt(
            parseInt(modifiedR, 2),
            parseInt(modifiedG, 2),
            parseInt(modifiedB, 2),
            rgba.a
            );
            modifiedImage.setPixelColor(modifiedColor, x, y);
            
            bitIndex = (bitIndex + 3) % binaryMessageWithDelimiter.length;
          }
        }
        setImg(modifiedImage)
      })

      // const encodedImagePath = 'output.png';
      // image.write(encodedImagePath, () => {
      //   console.log('Message encoded successfully. Encoded image saved at:', encodedImagePath);
      // });
      
};



  const handleSend=async()=>{
    if(img){
        const storageRef = ref(storage, uuid());
        const uploadTask = uploadBytesResumable(storageRef, img);

        const reader = new FileReader();
      reader.onload = async () => {
        const imageDataUrl = reader.result;
        const image = new Image();
        image.src = imageDataUrl;
        await image.decode();
        console.log("imageurl:"+imageDataUrl);

        // const canvas = document.createElement('canvas');
        // canvas.width = image.width;
        // canvas.height = image.height;
    //     const link = document.createElement('a');
    // link.href = imageDataUrl;
    // link.download = 'qrcode.png';
    // link.click();
  
  encodeMessage(imageDataUrl,"hello");}
      reader.readAsDataURL(img);
        console.log(img);
        // encodeMessage(img,"hello");
  
        uploadTask.on(
          (error) => {
            // setErr(true);
          },
          () => {
            getDownloadURL(uploadTask.snapshot.ref).then(async (downloadURL) => {
              await updateDoc(doc(db, "chats", data.chatId), {
                messages: arrayUnion({
                  id: uuid(),
                  text,
                  senderId: currentUser.uid,
                  date: Timestamp.now(),
                  img: downloadURL,
                }),
              });
            });
          }
        );
    } else{
      await updateDoc(doc(db,"chats",data.chatId),{
        messages:arrayUnion({
          id:uuid(),
          text,
          senderId:currentUser.uid,
          date:Timestamp.now()
        })
      })
    }
    await updateDoc(doc(db,"userChats",currentUser.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })
    await updateDoc(doc(db,"userChats",data.user.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })

    setText("")
    setImg(null)
  }
  const handleChange=(e)=>{
    setImg(e.target.files[0])
  }
  return (
    <div className='input'>
      <input type="text" placeholder='Type...' onChange={e=>setText(e.target.value)} value={text} />
      <div className="send">
        <input type="file" style={{display:'none'}} id='file' onChange={handleChange}/>
        <label htmlFor="file">
          <img src={im} alt=""/>
        </label>
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  )
}
export default Input
