import React, { useContext, useState } from 'react'
import at from 'D:/VS_Code/VS_Code_general_Workspace/finalYearProject/chatApp/src/images/at.png'
import im from 'D:/VS_Code/VS_Code_general_Workspace/finalYearProject/chatApp/src/images/im.jpg'
import { AuthContext } from '../context/AuthContext'
import { ChatContext } from '../context/ChatContext'
import { Timestamp, arrayUnion, doc, serverTimestamp, updateDoc } from 'firebase/firestore'
import { db, storage } from '../firebase'
import { v4 as uuid } from 'uuid'
import { getDownloadURL, ref, uploadBytesResumable } from 'firebase/storage'

const Input = () => {
  const [text,setText]=useState("")
  const [img,setImg]=useState(null)
  const {currentUser}=useContext(AuthContext)
  const {data}=useContext(ChatContext)


const Jimp = require('jimp');
const encodeMessage = (image, message, delimiter) => {
  // Convert the message to binary
  const binaryMessage = message.split('')
    .map(char => char.charCodeAt(0).toString(2).padStart(8, '0'))
    .join('');

  // Add the delimiter to the end of the binary message
  const binaryMessageWithDelimiter = binaryMessage + delimiter;

  // Read the original image using Jimp
      // Get the dimensions of the image
      const width = image.bitmap.width;
      const height = image.bitmap.height;

      let binaryMessageIndex = 0;
      for (let y = 0; y < height; y++) {
        for (let x = 0; x < width; x++) {
          // Get the RGB values of the current pixel
          const rgba = Jimp.intToRGBA(image.getPixelColor(x, y));
          let binaryR = rgba.r.toString(2).padStart(8, '0');
          let binaryG = rgba.g.toString(2).padStart(8, '0');
          let binaryB = rgba.b.toString(2).padStart(8, '0');

          // Modify the least significant bit of each RGB channel to encode the message
          if (binaryMessageIndex < binaryMessageWithDelimiter.length) {
            binaryR = binaryR.slice(0, -1) + binaryMessageWithDelimiter[binaryMessageIndex++];
          }
          if (binaryMessageIndex < binaryMessageWithDelimiter.length) {
            binaryG = binaryG.slice(0, -1) + binaryMessageWithDelimiter[binaryMessageIndex++];
          }
          if (binaryMessageIndex < binaryMessageWithDelimiter.length) {
            binaryB = binaryB.slice(0, -1) + binaryMessageWithDelimiter[binaryMessageIndex++];
          }

          // Update the pixel with the modified RGB values
          const modifiedColor = Jimp.rgbaToInt(
            parseInt(binaryR, 2),
            parseInt(binaryG, 2),
            parseInt(binaryB, 2),
            rgba.a
          );
          image.setPixelColor(modifiedColor, x, y);
        }
      }

      // Save the encoded image
      // const encodedImagePath = imagePath.replace('.png', '_encoded.png');

      const encodedImagePath = 'output.png';
      image.write(encodedImagePath, () => {
        console.log('Message encoded successfully. Encoded image saved at:', encodedImagePath);
      });
};




  const handleSend=async()=>{
    if(img){
        const storageRef = ref(storage, uuid());
  
        const uploadTask = uploadBytesResumable(storageRef, img);
  
        uploadTask.on(
          (error) => {
            // setErr(true);
          },
          () => {
            getDownloadURL(uploadTask.snapshot.ref).then(async (downloadURL) => {
              await updateDoc(doc(db, "chats", data.chatId), {
                messages: arrayUnion({
                  id: uuid(),
                  text,
                  senderId: currentUser.uid,
                  date: Timestamp.now(),
                  img: downloadURL,
                }),
              });
            });
          }
        );
    } else{
      await updateDoc(doc(db,"chats",data.chatId),{
        messages:arrayUnion({
          id:uuid(),
          text,
          senderId:currentUser.uid,
          date:Timestamp.now()
        })
      })
    }
    await updateDoc(doc(db,"userChats",currentUser.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })
    await updateDoc(doc(db,"userChats",data.user.uid),{
      [data.chatId+".lastMessage"]:{
        text
      },
      [data.chatId+".date"]:serverTimestamp()
    })

    setText("")
    setImg(null)
  }
  return (
    <div className='input'>
      <input type="text" placeholder='Type...' onChange={e=>setText(e.target.value)} value={text} />
      <div className="send">
        {/* <img src={at} alt="" /> */}
        <input type='file' style={{display:'none'}} id='file' onChange={e=>setImg(e.target.files[0])}/>
        <label htmlFor="file">
          <img src={im} alt=""/>
        </label>
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
  )
}
export default Input
