import React,{useState} from 'react'
import icon from "D:/VS_Code/VS_Code_general_Workspace/finalYearProject/chatApp/src/images/user.jpg"
import { collection, query, where,getDocs } from "firebase/firestore";
import {db} from '../firebase'


const Search = () => {
  const [userName,setUserName]=useState("")
  const [user,setUser]=useState(null)
  const [err,setErr]=useState(false)

  const handleSearch=async()=>{
    const q=query(collection(db,"user"),where("displayName","==",userName))

    try{
      const querySnapshot = await getDocs(q);
      querySnapshot.forEach((doc) => {
        setUser(doc.data())
      });
    }catch(err){
      setErr(true)
    }
    };
  const handleKey = e=>{
    e.code==="Enter" && handleSearch();
  };
  return (
    <div className='search'>
      <div className="searchForm">
        <input type="text" placeholder='Search' onKeyDown={handleKey} onChange={e=>setUserName(e.target.value)}/>
        <div className="userChat">
          <img src={icon} alt="" />
          <div className="userChatInfo">
            <span>Name1</span>
          </div>
        </div>
      </div>
    </div>

  )
}

export default Search
